    private void ConversionButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ConversionButtonActionPerformed
        // TODO add your handling code here:
        String inputText = InputDataField.getText();
        clearOutput();
        if(inputIsValid(inputText)){
            Double Tcel = Double.valueOf(InputDataField.getText());
            Double Tfah = 1.8*Tcel+32;
            OutputDataField.setText(""+Tfah);
        }
    }//GEN-LAST:event_ConversionButtonActionPerformed
    private void clearOutput(){
        OutputDataField.setText("");
    }
    private boolean inputIsValid(String inputText){
        if(inputText.isEmpty()) return false;
        if(!isNumeric(inputText)) return false;
        if(isBelowZero(inputText)) return false;
       return true;
    }
    private static boolean isNumeric(String str){  
        try{  
            double d = Double.parseDouble(str);  
        }  
        catch(NumberFormatException nfe){  
            return false;  
        }  
        return true;  
    }
    private boolean isBelowZero(String str) {
        double d = Double.parseDouble(str);  
        return d<-273.15;
    }
